import React from 'react'
import { useState, useEffect } from "react";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import {useSelector, useDispatch} from 'react-redux';
import { Button, Form , Card} from "react-bootstrap";
import API from "./APL";
import './session.css';
import {  Modal } from 'react-bootstrap';
import moment from 'moment';
import { Link } from 'react-router-dom';
import  { Redirect } from 'react-router-dom'
import {  Row, Col } from 'react-grid';




const Sesion = () => {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("")
  const [date, setDate] = useState("");
  const [time, setTime] = useState("");
  const [link, setLink] = useState("");

  const [sessionId, setSessionId] = useState(null);
  const [sessions, setSessions] = useState([]);

  const auth= useSelector((state) => state.auth)
  const dispatch=useDispatch()
  console.log(auth);

  useEffect(() => {
    refreshSessions();
  }, []);

  const refreshSessions = () => {
    API.get("/")
      .then((res) => {
        setSessions(res.data);
      })
      .catch(console.error);
  };

  const onSubmit = (e) => {
    e.preventDefault();
    // e.target.reset();
    let item = { title, description, date, time, link };
    API.post("/", item).then(() => refreshSessions());
    setTitle("")
    setDescription("")
    setDate("")
    setTime("")
    setLink("")
  };

  const onUpdate = (id) => {
    let item = { title, description, date, time, link };
    API.patch(`/${id}/`, item).then((res) => refreshSessions());
    setTitle("")
    setDescription("")
    setDate("")
    setTime("")
    setLink("")
  }
  const onDelete = (id) => {
    API.delete(`/${id}/`).then((res) => refreshSessions());
  };
  function selectSession(id) {
    let item = sessions.filter((session) => session.id === id)[0];
    setTitle(item.title);
    setDescription(item.description);
    setDate(item.date);
    setTime(item.time);
    setLink(item.link);
    setSessionId(item.id);
  }
  const [isOpen, setIsOpen] = React.useState(false);
  const showModal = () => {
    setIsOpen(true);
  };
  const hideModal = () => {
    setIsOpen(false);
  };


  return (
    <Row className='mt-2'>   
    <Col className="announcement mt-4"  sm={2} style={{background: "#18183D",height: "800px", color:"white", overflow: 'hidden',position:"fixed", }}>
            <li style={{listStyleType: "none"}} className='nav-item'> <hr />
              <Link to='/announcements' className='nav-links' >
                Announcements
              </Link>
            </li>
            <li style={{listStyleType: "none"}} className='nav-item'>  <hr />
              <Link to='/sesion' className='nav-links' >
                Sessions
              </Link>
            </li>


            <li style={{listStyleType: "none"}} className='nav-item'> <hr />
              <Link
                to='/schedule'
                className='nav-links'

              >
                Schedule
              </Link>
            </li> <hr />

            <li className='nav-item' style={{listStyleType: "none"}}>

              {auth.isStudent ? null : <>
                <Link to='/addstudent' className='nav-links' >
                Add Student
              </Link>

           </>
             }

            </li>
            <li className='nav-item' style={{listStyleType: "none"}}>

              {auth.isStudent ? null : <>
                <Link to='/newannouncement' className='nav-links' >
                Add Announcement
              </Link>

           </>
             }

            </li>


</Col>
<Col className=''  style={{marginLeft:'240px'}} sm={10}>

    <div className=''>
        <div className="container mt-5">
      <div className="row  page">

      {auth.isStudent ? null:<>
        <div className="col-md-4 mt-5 ">

        <Button onClick={showModal} className='btn' style={{backgroundColor: "#18183D",color:'white',fontWeight:'bold'}}> Add a New Session</Button>

        <Modal show={isOpen} onHide={hideModal}>
        <Modal.Header>
          <Modal.Title><h1>New Session </h1></Modal.Title>
        </Modal.Header>
          <Form onSubmit={onSubmit} className="mt-4" style={{padding:'20px',backgroundColor:'whitesmoke'}}>
            <Form.Group className="mb-3" controlId="formBasicTitle">
              <Form.Label>{sessionId}Session Title</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter The Title"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
              />
            </Form.Group>

            <Form.Group className="mb-3" controlId="formBasicDetscription">
              <Form.Label>Session Details</Form.Label>
              <Form.Control as="textarea" rows={3}
                type="textarea"
                placeholder="Enter Session Details"
                value={description}
                onChange={(e) => setDescription(e.target.value)}
