import './Announcement.css';
import React, { useState, useEffect }  from 'react';
import {  Row, Col } from 'react-grid';
import Container from 'react-bootstrap/Container';
import 'bootstrap/dist/css/bootstrap.min.css';
import Card from 'react-bootstrap/Card'  
import List from './List';
import withListLoading from './withListLoading';


const Announcement = () => {
  const ListLoading = withListLoading(List);
  const [appState, setAppState] = useState({
    loading: false,
    repos: null,
  });

  useEffect(() => {
    setAppState({ loading: true });
    const apiUrl = `https://class-schedule-app00.herokuapp.com/api/announcements/`;
    fetch(apiUrl)
      .then((res) => res.json())
      .then((announcements) => {
        setAppState({ loading: false, announcements: announcements });
      });
  }, [setAppState]);
    return (
<Container fluid>



<Row style={{ height: "100%", marginLeft:"-2rem" }}>
    <Col sm={2} style={{background: "#18183D", color:"white", overflow: 'hidden', position:"fixed", marginTop: "4.5rem", height: "100%" }} >
    <hr />
    <h3 style={{marginLeft: "30%", paddingTop: "10%"}}>All</h3> 
 <hr />
    <a style={{marginLeft: "20%", paddingTop: "10%", textDecoration: "none", color: "#FFF", fontSize: "20px"}}href="/comments">Comments</a> <hr />
    <Card.Link style={{color: "white", opacity: "0.8", fontSize: "20px", textDecoration: "none", marginLeft: "13%"}}href="/sessions">Announcements</Card.Link>   
<hr />


</Col>
<Col sm={10}><h2>| Announcements</h2>
<div className='repo-container' style={{marginLeft: "20%", marginTop: "5%"}}>
      <ListLoading isLoading={appState.loading} announcements={appState.announcements} />
    </div> 
    <footer>
      <div className='footer'  style={{marginLeft: "21%", marginTop: "5%"}}>
        Built with {' '}
        <span role='img' aria-label='love'>
          ðŸ’š
        </span>{' '}
         by Group 10
      </div>
    </footer>






</Col>

</Row>
</Container>



    );
};

export default Announcement;
