import './Announcement.css';
import React, { useState, useEffect }  from 'react';
import {  Row, Col } from 'react-grid';
import Container from 'react-bootstrap/Container';
import 'bootstrap/dist/css/bootstrap.min.css';
import Card from 'react-bootstrap/Card'  
import List from './List';
import withListLoading from './withListLoading';
import { Link } from 'react-router-dom';



const auth= useSelector((state) => state.auth)
const dispatch=useDispatch()
console.log(auth);

const Announcement = () => {
  const ListLoading = withListLoading(List);
  const [appState, setAppState] = useState({
    loading: false,
    repos: null,
  });

  useEffect(() => {
    setAppState({ loading: true });
    const apiUrl = `https://class-schedule-app00.herokuapp.com/api/announcements/`;
    fetch(apiUrl)
      .then((res) => res.json())
      .then((announcements) => {
        setAppState({ loading: false, announcements: announcements });
      });
  }, [setAppState]);
    return (
<Container fluid>


<Row>   
<Col className="announcement"  sm={2} style={{background: "#18183D",height: "760px", color:"white", overflow: 'hidden',position:"fixed", marginTop: "5.0rem"}}>

{auth.isAuthenticated ?
          <>
            <li className='nav-item'>
              <Link to='/announcements' className='nav-links' onClick={closeMobileMenu}>
                Announcements
              </Link>
            </li>
            <li className='nav-item'>
              <Link to='/sesion' className='nav-links' onClick={closeMobileMenu}>
                Sessions
              </Link>
            </li>

            <li className='nav-item'>
              <Link
                to='/profile'
                className='nav-links'
                onClick={closeMobileMenu}
              >
                Profile
              </Link>
            </li>

            <li className='nav-item'>
              <Link
                to='/schedule'
                className='nav-links'
                onClick={closeMobileMenu}
              >
                Schedule
              </Link>
            </li>
           </>: <>
           <li className='nav-item' style={{position: "absolute", top:"0", marginLeft: "68%"}}>
              <Link
                to='/login'
                className='nav-links btn-login'
                onClick={closeMobileMenu}
              >
                Login
              </Link>
            </li>

            <li className='btn-signup'>
              <Link
                to='/signup'
                className='nav-links-mobile'
                onClick={closeMobileMenu}
              >
                Create Student
              </Link>
            </li>
           </>}

</Col>
<Col sm={10}><h2>| Announcements</h2>
<div className='repo-container' style={{marginLeft: "20%", marginTop: "5%"}}>
      <ListLoading isLoading={appState.loading} announcements={appState.announcements} />
    </div> 
    <footer>
      <div className='footer'  style={{marginLeft: "21%", marginTop: "5%"}}>
        Built with {' '}
        <span role='img' aria-label='love'>
          ðŸ’š
        </span>{' '}
         by Group 10
      </div>
    </footer>




</Col>

</Row>
</Container>



    );
};

export default Announcement;
